// src/pages/AccountPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ModalMessage from '../components/ModalMessage';
import './AccountPage.css';

function AccountPage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');

  const [modal, setModal] = useState({ show: false, title: '', message: '' });

  const handleCloseModal = () => {
    setModal({ show: false, title: '', message: '' });
  };

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
      return;
    }
    setIsLoggedIn(true);

    fetchUserData(token);
  }, []);

  const fetchUserData = async (token) => {
    try {
      // Якщо є endpoint GET /api/users/profile
      const res = await axios.get('http://localhost:5000/api/users/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setLogin(res.data.login);
    } catch (error) {
      setModal({ show:true, title:'Помилка', message:'Не вдалося отримати дані профілю' });
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.patch('http://localhost:5000/api/users/update', {
        login,
        password
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setModal({ show:true, title:'Успіх', message:'Дані оновлено' });
      setPassword('');
    } catch (error) {
      setModal({ show:true, title:'Помилка', message:'Не вдалося оновити дані' });
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="account-container">
        <h2>Будь ласка, увійдіть у систему</h2>
      </div>
    );
  }

  return (
    <div className="account-container">
      <ModalMessage
        show={modal.show}
        onClose={handleCloseModal}
        title={modal.title}
        message={modal.message}
      />

      <h2>Мій акаунт</h2>
      <form className="account-form" onSubmit={handleUpdate}>
        <label>Новий Логін:</label>
        <input
          type="text"
          value={login}
          onChange={e => setLogin(e.target.value)}
        />
        <label>Новий Пароль:</label>
        <input
          type="password"
          placeholder="За бажанням"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button type="submit" className="update-button">Оновити</button>
      </form>
    </div>
  );
}

export default AccountPage;
