// src/pages/CartPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function CartPage() {
  const [cart, setCart] = useState(null);
  const token = localStorage.getItem('token');

  const fetchCart = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/cart', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart(res.data);
    } catch (error) {
      console.error('Помилка отримання кошика:', error);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const removeItem = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/api/cart/remove/${productId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart();
    } catch (error) {
      console.error('Помилка видалення товару:', error);
    }
  };

  const handleCheckout = async () => {
    try {
      const intentRes = await axios.post('http://localhost:5000/api/payment/create-intent', {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const { clientSecret, amount } = intentRes.data;
      console.log('ClientSecret:', clientSecret, 'Сума:', amount);

      // Імітація успішної оплати
      await axios.post('http://localhost:5000/api/payment/confirm-order', {
        paymentId: 'testPaymentId'
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Замовлення успішно створено!');
      fetchCart();
    } catch (error) {
      console.error('Помилка оформлення:', error);
    }
  };

  if (!cart) return <div>Завантаження...</div>;

  return (
    <div>
      <h2>Мій кошик</h2>
      {cart.items && cart.items.length > 0 ? (
        <div>
          {cart.items.map(item => (
            <div key={item._id} style={{ border: '1px solid #eee', marginBottom: '8px', padding: '8px' }}>
              {item.product?.name} (x{item.quantity})
              <button style={{ marginLeft: '10px' }} onClick={() => removeItem(item.product._id)}>
                Видалити
              </button>
            </div>
          ))}
          <button onClick={handleCheckout}>Оплатити</button>
        </div>
      ) : (
        <p>Кошик порожній</p>
      )}
    </div>
  );
}

export default CartPage;
