// src/pages/CartPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './CartPage.css';

function CartPage() {
  const [cart, setCart] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
    } else {
      setIsLoggedIn(true);
      fetchCart(token);
    }
  }, []);

  const fetchCart = async (token) => {
    try {
      const response = await axios.get('http://localhost:5000/api/cart', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart(response.data);
    } catch (error) {
      console.error('Помилка отримання кошика:', error);
    }
  };

  const removeItem = async (productId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/cart/remove/${productId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart(token);
    } catch (error) {
      console.error('Помилка видалення товару:', error);
    }
  };

  const handleCheckout = async () => {
    try {
      const token = localStorage.getItem('token');
      const intentRes = await axios.post('http://localhost:5000/api/payment/create-intent', {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const { clientSecret, amount } = intentRes.data;
      console.log('ClientSecret:', clientSecret, 'Сума:', amount);

      // Імітація успішної оплати
      await axios.post('http://localhost:5000/api/payment/confirm-order', {
        paymentId: 'testPaymentId'
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      alert('Замовлення успішно створено!');
      fetchCart(token);
    } catch (error) {
      console.error('Помилка оформлення:', error);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="cart-container fade-in-cart">
        <div className="unauthorized-message">
          <h2>Лише для авторизованих користувачів</h2>
          <p>Будь ласка, <Link to="/register">зареєструйтесь</Link> або увійдіть у систему.</p>
        </div>
      </div>
    );
  }

  if (!cart) {
    return <div className="cart-container fade-in-cart">Завантаження...</div>;
  }

  return (
    <div className="cart-container fade-in-cart">
      <h2 className="cart-title">Мій кошик</h2>
      {cart.items && cart.items.length > 0 ? (
        <div className="cart-items-list">
          {cart.items.map(item => (
            <div key={item._id} className="cart-item">
              <span>{item.product?.name} (x{item.quantity})</span>
              <button onClick={() => removeItem(item.product._id)}>Видалити</button>
            </div>
          ))}
          <button className="checkout-btn" onClick={handleCheckout}>Оплатити</button>
        </div>
      ) : (
        <p>Кошик порожній</p>
      )}
    </div>
  );
}

export default CartPage;
