// src/pages/CartPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './CartPage.css';

function CartPage() {
  const [cart, setCart] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // Поля для форми оформлення замовлення
  const [showCheckoutForm, setShowCheckoutForm] = useState(false);
  const [deliveryAddress, setDeliveryAddress] = useState('');
  const [deliveryMethod, setDeliveryMethod] = useState('nova-poshta'); // 'nova-poshta' or 'ukrposhta'
  const [paymentMethod, setPaymentMethod] = useState('card'); // 'card' or 'cash'
  const [phoneNumber, setPhoneNumber] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
    } else {
      setIsLoggedIn(true);
      fetchCart(token);
    }
  }, []);

  const fetchCart = async (token) => {
    try {
      const response = await axios.get('http://localhost:5000/api/cart', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart(response.data);
    } catch (error) {
      console.error('Помилка отримання кошика:', error);
    }
  };

  const removeItem = async (productId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/cart/remove/${productId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart(token);
    } catch (error) {
      console.error('Помилка видалення товару:', error);
    }
  };

  // При натисканні "Оформити замовлення" показуємо форму
  const handleShowCheckout = () => {
    setShowCheckoutForm(true);
  };

  // При сабміті форми - імітація підтвердження замовлення
  const handleConfirmOrder = (e) => {
    e.preventDefault();
    // Тут можна викликати бекенд і передати дані доставки, якщо треба
    // Але для прикладу просто alert:
    alert('Замовлення успішне.\nОчікуйте, з вами зв’яжуться для підтвердження!');
    // Можна очистити кошик чи скинути форму:
    setShowCheckoutForm(false);
    setDeliveryAddress('');
    setDeliveryMethod('nova-poshta');
    setPaymentMethod('card');
    setPhoneNumber('');
  };

  if (!isLoggedIn) {
    return (
      <div className="cart-container fade-in-cart">
        <div className="unauthorized-message">
          <h2>Лише для авторизованих користувачів</h2>
          <p>Будь ласка, <Link to="/register">зареєструйтесь</Link> або увійдіть у систему.</p>
        </div>
      </div>
    );
  }

  if (!cart) {
    return <div className="cart-container fade-in-cart">Завантаження...</div>;
  }

  return (
    <div className="cart-container fade-in-cart">
      <h2 className="cart-title">Мій кошик</h2>
      {cart.items && cart.items.length > 0 ? (
        <div>
          <div className="cart-items-list">
            {cart.items.map(item => (
              <div key={item._id} className="cart-item">
                <div className="cart-item-details">
                  {item.product?.imagePath ? (
                    <img
                      src={`http://localhost:5000/${item.product?.imagePath}`}
                      alt={item.product?.name}
                      className="cart-item-image"
                    />
                  ) : (
                    <div className="cart-no-image">(Немає фото)</div>
                  )}

                  <div className="cart-item-info">
                    <p className="item-name">{item.product?.name}</p>
                    <p className="item-price">Ціна: ${item.product?.price}</p>
                    <p className="item-quantity">Кількість: {item.quantity}</p>
                  </div>
                </div>
                <button onClick={() => removeItem(item.product._id)}>Видалити</button>
              </div>
            ))}
          </div>

          {/* Кнопка "Оформити замовлення" */}
          {!showCheckoutForm && (
            <button className="checkout-btn" onClick={handleShowCheckout}>
              Оформити замовлення
            </button>
          )}

          {/* Форма оформлення замовлення */}
          {showCheckoutForm && (
            <form className="checkout-form" onSubmit={handleConfirmOrder}>
              <h3>Дані для доставки</h3>
              <label>Адреса доставки:</label>
              <input
                type="text"
                value={deliveryAddress}
                onChange={e => setDeliveryAddress(e.target.value)}
                required
              />
              <label>Спосіб доставки:</label>
              <select
                value={deliveryMethod}
                onChange={e => setDeliveryMethod(e.target.value)}
              >
                <option value="nova-poshta">Нова Пошта</option>
                <option value="ukrposhta">Укрпошта</option>
              </select>
              <label>Спосіб оплати:</label>
              <select
                value={paymentMethod}
                onChange={e => setPaymentMethod(e.target.value)}
              >
                <option value="card">Карткою</option>
                <option value="cash">Готівкою</option>
              </select>
              <label>Номер телефону:</label>
              <input
                type="tel"
                value={phoneNumber}
                onChange={e => setPhoneNumber(e.target.value)}
                required
              />
              <button type="submit" className="confirm-order-btn">Підтвердити замовлення</button>
            </form>
          )}
        </div>
      ) : (
        <p>Кошик порожній</p>
      )}
    </div>
  );
}

export default CartPage;
