import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './NavBar.css';

function NavBar() {
  const [role, setRole] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const r = localStorage.getItem('role') || '';
    if (token) {
      setIsLoggedIn(true);
      setRole(r);
    } else {
      setIsLoggedIn(false);
      setRole('');
    }
  }, []);

  return (
    <nav className="nav-bar fade-in-animation">
      <div className="nav-logo">Edems</div>
      <ul className="nav-links">
        <li><Link to="/">Головна</Link></li>
        {isLoggedIn ? (
          <>
            <li><Link to="/account">Акаунт</Link></li>
            {/* Кнопка "Кошик" відображається лише для звичайних користувачів */}
            {role !== 'admin' && <li><Link to="/cart">Кошик</Link></li>}
            {role === 'admin' && <li><Link to="/admin">Адмін-панель</Link></li>}
          </>
        ) : (
          <>
            <li><Link to="/login">Увійти</Link></li>
            <li><Link to="/register">Реєстрація</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
}

export default NavBar;
