import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './NavBar.css';

function NavBar() {
  const [role, setRole] = useState('');
  useEffect(() => {
    const r = localStorage.getItem('role') || '';
    setRole(r);
  }, []);

  return (
    <nav className="nav-bar fade-in-animation">
      <div className="nav-logo">Edems</div>
      <ul className="nav-links">
        <li><Link to="/">Головна</Link></li>
        <li><Link to="/login">Увійти</Link></li>
        <li><Link to="/register">Реєстрація</Link></li>
        {/* Якщо користувач не є адміном, показуємо кошик */}
        {role !== 'admin' && role !== '' && <li><Link to="/cart">Кошик</Link></li>}
        {/* Якщо користувач має роль admin, показуємо адмін-панель */}
        {role === 'admin' && <li><Link to="/admin">Адмін-панель</Link></li>}
      </ul>
    </nav>
  );
}

export default NavBar;
