// src/pages/HomePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HomePage.css';

function HomePage() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products');
      setProducts(res.data);
    } catch (error) {
      console.error('Помилка отримання товарів:', error);
    }
  };

  const addToCart = async (productId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Спочатку авторизуйтеся або зареєструйтеся, щоб додавати товари у кошик.');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/cart/add', {
        productId,
        quantity: 1
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Товар додано до кошика!');
    } catch (error) {
      console.error('Помилка додавання до кошика:', error);
      alert('Не вдалося додати товар. Можливо, треба увійти повторно?');
    }
  };

  return (
    <div className="home-page">
      <h1 className="site-title">Edems</h1>
      <p className="site-description">
        Тут можна знайти все необхідне для догляду за шкірою, макіяжу
        та отримати справжнє задоволення від покупок!
      </p>
      <p className="small-note">
        Нижче представлені наші товари:
      </p>

      <div className="home-catalog">
        {products.map(prod => (
          <div key={prod._id} className="home-product-card">
            <h3>{prod.name}</h3>
            {prod.imagePath ? (
              <img
                src={`http://localhost:5000/${prod.imagePath}`}
                alt={prod.name}
                className="home-product-image"
              />
            ) : (
              <div className="no-image-box">(Немає фото)</div>
            )}
            <p className="price-label">Ціна: ${prod.price}</p>
            <button onClick={() => addToCart(prod._id)}>В кошик</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
