// src/pages/RegisterPage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import './AuthForms.css';

function RegisterPage() {
  const [login, setLogin] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      // Припустимо, що сервер теж приймає поле "login"
      await axios.post('http://localhost:5000/api/users/register', {
        login,
        email,
        password
      });
      alert(`Реєстрація успішна!\nВітаємо, ${login}!\nТепер можна увійти.`);
      window.location.href = '/login';
    } catch (error) {
      console.error('Помилка реєстрації:', error);
      alert('Помилка реєстрації: ' + (error.response?.data?.message || 'Невідома'));
    }
  };

  return (
    <div className="auth-container fade-in-animation">
      <h2 className="auth-title">Реєстрація</h2>
      <form onSubmit={handleRegister} className="auth-form">
        <label>Логін</label>
        <input
          type="text"
          placeholder="Введіть логін"
          value={login}
          onChange={e => setLogin(e.target.value)}
          required
          style={{ fontSize: '1.1rem' }}
        />
        <label>Email</label>
        <input
          type="email"
          placeholder="Введіть email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
          style={{ fontSize: '1.1rem' }}
        />
        <label>Пароль</label>
        <input
          type="password"
          placeholder="Введіть пароль"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
          style={{ fontSize: '1.1rem' }}
        />
        <button type="submit" className="auth-button" style={{ fontSize: '1.2rem' }}>
          Зареєструватися
        </button>
      </form>
    </div>
  );
}

export default RegisterPage;
