import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ModalMessage from '../components/ModalMessage';
import './HomePage.css';

function HomePage() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [categories, setCategories] = useState([]);

  const [modal, setModal] = useState({ show: false, title: '', message: '' });
  const closeModal = () => setModal({ show: false, title: '', message: '' });

  const fetchProducts = async () => {
    try {
      // Запит з параметрами пошуку та фільтрації
      const res = await axios.get('http://localhost:5000/api/products', {
        params: { search, category: categoryFilter }
      });
      setProducts(res.data);

      // Додатково – для динамічного списку категорій
      const allRes = await axios.get('http://localhost:5000/api/products');
      const allProducts = allRes.data;
      const catSet = new Set();
      allProducts.forEach(p => {
        if (p.category) catSet.add(p.category);
      });
      setCategories(Array.from(catSet));
    } catch (error) {
      setModal({ show: true, title: 'Помилка', message: 'Не вдалося завантажити товари' });
    }
  };

  useEffect(() => {
    fetchProducts();
    // eslint-disable-next-line
  }, [search, categoryFilter]);

  const handleFormChange = (e) => {
    // Використовується, якщо потрібні додаткові поля для фільтрації
    // Тут можна не використовувати
  };

  return (
    <div className="home-page">
      <ModalMessage
        show={modal.show}
        onClose={closeModal}
        title={modal.title}
        message={modal.message}
      />
      <h1 className="site-title">Edems</h1>
      <p className="site-description">
        Тут можна знайти все необхідне для догляду за шкірою та макіяжу!
      </p>
      <div className="filter-section">
        <input
          type="text"
          placeholder="Пошук..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <select
          value={categoryFilter}
          onChange={e => setCategoryFilter(e.target.value)}
        >
          <option value="">Усі категорії</option>
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>
      <div className="home-catalog">
        {products.map(prod => (
          <div key={prod._id} className="home-product-card">
            <h3>{prod.name}</h3>
            {prod.imagePath ? (
              <img
                src={`http://localhost:5000/${prod.imagePath}`}
                alt={prod.name}
                className="home-product-image"
              />
            ) : (
              <div className="no-image-box">(Немає фото)</div>
            )}
            <p className="price-label">Ціна: ${prod.price}</p>
            <p className="category-label">{prod.category}</p>
            <p className="description">{prod.description}</p>
          </div>
        ))}
      </div>

      <div className="create-product-form">
        <h2>Додати товар</h2>
        <form onSubmit={async (e) => {
          e.preventDefault();
          try {
            const data = new FormData();
            data.append('name', e.target.name.value);
            data.append('description', e.target.description.value);
            data.append('price', e.target.price.value);
            data.append('category', e.target.category.value);
            if (e.target.image.files[0]) {
              data.append('image', e.target.image.files[0]);
            }
            await axios.post('http://localhost:5000/api/products', data, {
              headers: { 'Content-Type': 'multipart/form-data' }
            });
            setModal({ show: true, title: 'Успіх', message: 'Товар створено!' });
            e.target.reset();
            fetchProducts();
          } catch (error) {
            setModal({ show: true, title: 'Помилка', message: 'Не вдалося створити товар' });
          }
        }}>
          <input type="text" name="name" placeholder="Назва" required />
          <input type="text" name="description" placeholder="Опис" />
          <input type="number" name="price" placeholder="Ціна" required />
          <input type="text" name="category" placeholder="Категорія" required />
          <input type="file" name="image" />
          <button type="submit">Додати товар</button>
        </form>
      </div>
    </div>
  );
}

export default HomePage;
