// src/pages/AccountPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ModalMessage from '../components/ModalMessage';
import { useNavigate } from 'react-router-dom';
import './AccountPage.css';

function AccountPage() {
  const navigate = useNavigate();

  const [login, setLogin] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(true);

  const [orders, setOrders] = useState([]);

  const [modal, setModal] = useState({ show: false, title: '', message: '' });
  const closeModal = () => setModal({ show: false, title: '', message: '' });

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      // Якщо немає токена, робимо редірект на головну (або логін)
      navigate('/', { replace: true });
      return;
    }
    // Завантажуємо профіль
    axios.get('http://localhost:5000/api/users/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        setLogin(res.data.login);
        setLoading(false);
      })
      .catch(() => {
        setLoading(false);
      });

    // Завантажуємо замовлення
    axios.get('http://localhost:5000/api/orders/my', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => {
        setOrders(res.data);
      })
      .catch(() => {
        // Якщо помилка, просто ігноруємо або показуємо повідомлення
      });
  }, [navigate]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.patch('http://localhost:5000/api/users/update', { login, password }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setModal({ show: true, title: 'Успіх', message: 'Дані оновлено' });
      setPassword('');
    } catch (error) {
      setModal({ show: true, title: 'Помилка', message: 'Не вдалося оновити дані' });
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/', { replace: true });
  };

  if (loading) {
    return (
      <div className="account-container">
        Завантаження...
      </div>
    );
  }

  return (
    <div className="account-container">
      <ModalMessage
        show={modal.show}
        onClose={closeModal}
        title={modal.title}
        message={modal.message}
      />
      <h2>Мій акаунт</h2>
      <form className="account-form" onSubmit={handleUpdate}>
        <label>Новий Логін:</label>
        <input
          type="text"
          value={login}
          onChange={e => setLogin(e.target.value)}
        />
        <label>Новий Пароль:</label>
        <input
          type="password"
          placeholder="За бажанням"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button type="submit" className="update-button">Оновити</button>
      </form>

      <button onClick={handleLogout} className="logout-button">Вийти</button>

      <hr />

      <h3>Мої замовлення</h3>
      {orders.length === 0 ? (
        <p>Поки немає жодного замовлення</p>
      ) : (
        orders.map(order => (
          <div key={order._id} className="order-item">
            <p><strong>ID замовлення:</strong> {order._id}</p>
            <p><strong>Статус:</strong> {order.status}</p>
            <p><strong>Сума:</strong> ${order.totalAmount}</p>
            <p><strong>Дата:</strong> {new Date(order.createdAt).toLocaleString()}</p>
            <hr />
          </div>
        ))
      )}
    </div>
  );
}

export default AccountPage;
