// src/pages/AccountPage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AccountPage.css';

function AccountPage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [orders, setOrders] = useState([]); // масив активних замовлень

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
      return;
    }
    setIsLoggedIn(true);

    // 1) Завантажити поточний email (або інші дані)
    fetchUserData(token);

    // 2) Завантажити активні замовлення
    fetchActiveOrders(token);
  }, []);

  const fetchUserData = async (token) => {
    try {
      // Припустимо, бекенд має endpoint GET /api/users/profile
      const res = await axios.get('http://localhost:5000/api/users/profile', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setEmail(res.data.email || '');
    } catch (error) {
      console.error('Помилка отримання даних користувача:', error);
    }
  };

  const fetchActiveOrders = async (token) => {
    try {
      // Припустимо, бекенд має endpoint GET /api/orders/my-active
      const res = await axios.get('http://localhost:5000/api/orders/my-active', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(res.data);
    } catch (error) {
      console.error('Помилка отримання замовлень:', error);
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      // Припустимо, бекенд має endpoint PATCH /api/users/update
      await axios.patch('http://localhost:5000/api/users/update', {
        email,
        password
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Дані оновлено успішно!');
      setPassword(''); // очистити поле пароля
    } catch (error) {
      console.error('Помилка оновлення даних:', error);
      alert('Не вдалося оновити дані. ' + (error.response?.data?.message || ''));
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="account-container">
        <h2>Будь ласка, увійдіть у систему</h2>
      </div>
    );
  }

  return (
    <div className="account-container">
      <h2>Мій акаунт</h2>
      <form className="account-form" onSubmit={handleUpdate}>
        <label>Новий Email:</label>
        <input
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
        />
        <label>Новий Пароль:</label>
        <input
          type="password"
          placeholder="За бажанням"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button type="submit" className="update-button">Оновити</button>
      </form>

      <h3>Мої активні замовлення</h3>
      {orders.length > 0 ? (
        <ul className="orders-list">
          {orders.map((order) => (
            <li key={order._id} className="order-item">
              <p><strong>ID замовлення:</strong> {order._id}</p>
              <p><strong>Статус:</strong> {order.status}</p>
              <p><strong>Товари:</strong> 
                {order.items.map((item) => (
                  <span key={item._id}> {item.product.name} (x{item.quantity})</span>
                ))}
              </p>
            </li>
          ))}
        </ul>
      ) : (
        <p>Немає активних замовлень</p>
      )}
    </div>
  );
}

export default AccountPage;
