// src/pages/HomePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ModalMessage from '../components/ModalMessage';
import './HomePage.css';

function HomePage() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [categories, setCategories] = useState([]);

  // Якщо треба показувати "В кошик" для user
  const [role, setRole] = useState(''); // з localStorage

  // Форма створення товару
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category: ''
  });
  const [selectedFile, setSelectedFile] = useState(null);

  const [modal, setModal] = useState({ show:false, title:'', message:'' });
  const closeModal = () => setModal({ show:false, title:'', message:'' });

  useEffect(() => {
    // зчитуємо роль
    const storedRole = localStorage.getItem('role');
    if (storedRole) setRole(storedRole);

    fetchProducts();
    // eslint-disable-next-line
  }, [search, categoryFilter]);

  const fetchProducts = async () => {
    try {
      // 1. Отримати відфільтрований список
      const res = await axios.get('http://localhost:5000/api/products', {
        params: { search, category: categoryFilter }
      });
      setProducts(res.data);

      // 2. Отримати всі товари, зібрати категорії
      const allRes = await axios.get('http://localhost:5000/api/products');
      const allProducts = allRes.data;
      const catSet = new Set();
      allProducts.forEach(p => {
        if (p.category) catSet.add(p.category);
      });
      setCategories(Array.from(catSet));
    } catch (error) {
      setModal({ show:true, title:'Помилка', message:'Не вдалося завантажити товари' });
    }
  };

  // Зміни в полях створення товару
  const handleFormChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  // Створення товару (для всіх?)
  const handleCreateProduct = async (e) => {
    e.preventDefault();
    try {
      const data = new FormData();
      data.append('name', formData.name);
      data.append('description', formData.description);
      data.append('price', formData.price);
      data.append('category', formData.category);
      if (selectedFile) data.append('image', selectedFile);

      await axios.post('http://localhost:5000/api/products', data, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setModal({ show:true, title:'Успіх', message:'Товар створено!' });
      setFormData({ name:'', description:'', price:'', category:'' });
      setSelectedFile(null);
      fetchProducts();
    } catch (error) {
      setModal({ show:true, title:'Помилка', message:'Не вдалося створити товар' });
    }
  };

  // Імітація «додавання в кошик»
  const addToCart = (productId) => {
    if (role === 'admin') {
      // admin не бачить кнопки, тож не натисне
      return;
    }
    // user
    setModal({ show:true, title:'В кошик', message:`Товар ${productId} додано в кошик! (фейк)` });
  };

  return (
    <div className="home-page">
      <ModalMessage
        show={modal.show}
        onClose={closeModal}
        title={modal.title}
        message={modal.message}
      />

      <h1 className="site-title">Головна сторінка</h1>

      {/* Пошук/фільтр */}
      <div className="filter-section">
        <input
          type="text"
          placeholder="Пошук..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <select
          value={categoryFilter}
          onChange={(e) => setCategoryFilter(e.target.value)}
        >
          <option value="">Усі категорії</option>
          {categories.map(cat => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
      </div>

      {/* Список товарів */}
      <div className="home-catalog">
        {products.map(prod => (
          <div key={prod._id} className="home-product-card">
            <h3>{prod.name}</h3>
            {prod.imagePath ? (
              <img
                src={`http://localhost:5000/${prod.imagePath}`}
                alt={prod.name}
                className="home-product-image"
              />
            ) : (
              <div className="no-image-box">(Немає фото)</div>
            )}
            <p className="price-label">Ціна: ${prod.price}</p>
            <p>Категорія: {prod.category}</p>
            <p style={{ fontStyle:'italic' }}>{prod.description}</p>

            {/* Кнопка "В кошик" лише якщо role !== 'admin' */}
            {role !== 'admin' && (
              <button className="add-to-cart-btn" onClick={() => addToCart(prod._id)}>
                В кошик
              </button>
            )}
          </div>
        ))}
      </div>

      {/* Форма створення товару */}
      <div className="create-product-form">
        <h2>Створити товар (Multer demo)</h2>
        <form onSubmit={handleCreateProduct}>
          <input
            type="text"
            name="name"
            placeholder="Назва"
            value={formData.name}
            onChange={handleFormChange}
            required
          />
          <input
            type="text"
            name="description"
            placeholder="Опис"
            value={formData.description}
            onChange={handleFormChange}
          />
          <input
            type="number"
            name="price"
            placeholder="Ціна"
            value={formData.price}
            onChange={handleFormChange}
            required
          />
          <input
            type="text"
            name="category"
            placeholder="Категорія"
            value={formData.category}
            onChange={handleFormChange}
          />
          <input type="file" onChange={handleFileChange} />
          <button type="submit">Додати товар</button>
        </form>
      </div>
    </div>
  );
}

export default HomePage;
