// src/pages/HomePage.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ModalMessage from '../components/ModalMessage';
import './HomePage.css';

function HomePage() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('');

  const [modal, setModal] = useState({ show: false, title: '', message: '' });

  const handleCloseModal = () => {
    setModal({ show: false, title: '', message: '' });
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products', {
        params: { search, category }
      });
      setProducts(res.data);
    } catch (error) {
      console.error('Помилка отримання товарів:', error);
      setModal({ show: true, title: 'Помилка', message: 'Не вдалося завантажити товари' });
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [search, category]);

  const addToCart = async (productId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      setModal({
        show: true,
        title: 'Інформація',
        message: 'Спочатку авторизуйтеся, щоб додавати товари у кошик.'
      });
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/cart/add', {
        productId,
        quantity: 1
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setModal({
        show: true,
        title: 'Успіх',
        message: 'Товар додано до кошика!'
      });
    } catch (error) {
      setModal({
        show: true,
        title: 'Помилка',
        message: 'Не вдалося додати товар до кошика'
      });
    }
  };

  return (
    <div className="home-page">
      <ModalMessage
        show={modal.show}
        onClose={handleCloseModal}
        title={modal.title}
        message={modal.message}
      />

      <h1 className="site-title">Edems</h1>
      <p className="site-description">
        Тут можна знайти все необхідне для догляду за шкірою, макіяжу
        та отримати справжнє задоволення від покупок!
      </p>

      <div className="filter-section">
        <input
          type="text"
          placeholder="Пошук..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <select
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="">Усі категорії</option>
          <option value="foundation">foundation</option>
          <option value="lipstick">lipstick</option>
          <option value="skincare">skincare</option>
          {/* ... */}
        </select>
      </div>

      <div className="home-catalog">
        {products.map(prod => (
          <div key={prod._id} className="home-product-card">
            <h3>{prod.name}</h3>
            {prod.imagePath ? (
              <img
                src={`http://localhost:5000/${prod.imagePath}`}
                alt={prod.name}
                className="home-product-image"
              />
            ) : (
              <div className="no-image-box">(Немає фото)</div>
            )}
            <p className="price-label">Ціна: ${prod.price}</p>
            <button className="add-to-cart-btn" onClick={() => addToCart(prod._id)}>
              В кошик
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
