// src/components/NavBar.jsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './NavBar.css';

function NavBar() {
  const [role, setRole] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userRole = localStorage.getItem('role');
    if (token) {
      setIsLoggedIn(true);
      if (userRole) {
        setRole(userRole);
      }
    } else {
      setIsLoggedIn(false);
      setRole(null);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    window.location.href = '/';
  };

  return (
    <nav className="nav-bar fade-in-animation">
      <div className="nav-logo">Edems</div>
      <ul className="nav-links">
        <li><Link to="/">Головна</Link></li>

        {/* Якщо користувач звичайний - посилання на кошик */}
        {isLoggedIn && role !== 'admin' && (
          <li><Link to="/cart">Кошик</Link></li>
        )}

        {/* Якщо авторизований (будь-яка роль) - посилання на Акаунт */}
        {isLoggedIn && (
          <li><Link to="/account">Акаунт</Link></li>
        )}

        {/* Якщо користувач - адміністратор */}
        {isLoggedIn && role === 'admin' && (
          <li><Link to="/admin">Адмін-панель</Link></li>
        )}

        {/* Якщо не залогінено */}
        {!isLoggedIn && (
          <>
            <li><Link to="/login">Увійти</Link></li>
            <li><Link to="/register">Реєстрація</Link></li>
          </>
        )}

        {/* Якщо залогінено - кнопка вийти */}
        {isLoggedIn && (
          <li>
            <button className="logout-btn" onClick={handleLogout}>Вийти</button>
          </li>
        )}
      </ul>
    </nav>
  );
}

export default NavBar;
