// src/pages/AdminPage.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminPage() {
  const [products, setProducts] = useState([]);
  const token = localStorage.getItem('token');

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category: ''
  });
  const [selectedFile, setSelectedFile] = useState(null);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products');
      setProducts(res.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    if (!token) {
      alert('Потрібно увійти під адміністратором');
      return;
    }
    try {
      // Використовуємо FormData для файлів
      const data = new FormData();
      data.append('name', formData.name);
      data.append('description', formData.description);
      data.append('price', formData.price);
      data.append('category', formData.category);
      if (selectedFile) {
        data.append('image', selectedFile); // поле 'image' як у multer
      }

      await axios.post('http://localhost:5000/api/products', data, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      alert('Товар створено!');
      setFormData({ name: '', description: '', price: '', category: '' });
      setSelectedFile(null);
      fetchProducts();
    } catch (error) {
      console.error(error);
      alert('Помилка створення товару');
    }
  };

  const handleDelete = async (id) => {
    if (!token) return;
    try {
      await axios.delete(`http://localhost:5000/api/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Товар видалено');
      fetchProducts();
    } catch (error) {
      console.error('Помилка видалення:', error);
    }
  };

  return (
    <div>
      <h2>Адмін-панель</h2>
      <form onSubmit={handleCreate} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          name="name"
          placeholder="Назва"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <br />
        <input
          type="text"
          name="description"
          placeholder="Опис"
          value={formData.description}
          onChange={handleChange}
        />
        <br />
        <input
          type="number"
          name="price"
          placeholder="Ціна"
          value={formData.price}
          onChange={handleChange}
          required
        />
        <br />
        <input
          type="text"
          name="category"
          placeholder="Категорія (foundation, lipstick, ...)"
          value={formData.category}
          onChange={handleChange}
          required
        />
        <br />
        <input type="file" onChange={handleFileChange} />
        <br />
        <button type="submit">Додати товар</button>
      </form>

      <h3>Список товарів</h3>
      <div>
        {products.map(p => (
          <div key={p._id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
            <h4>{p.name} (${p.price})</h4>
            <p>{p.description}</p>
            {p.imagePath ? (
              <img
                src={`http://localhost:5000/${p.imagePath}`}
                alt={p.name}
                style={{ width: '120px' }}
              />
            ) : (
              <div style={{ width: '120px', height: '120px', backgroundColor: '#eee' }}>
                (Немає фото)
              </div>
            )}
            <p><em>Категорія: {p.category}</em></p>
            <button onClick={() => handleDelete(p._id)}>Видалити</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AdminPage;
