import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ModalMessage from '../components/ModalMessage';
import './AdminPage.css';

function AdminPage() {
  const [products, setProducts] = useState([]);
  const [modal, setModal] = useState({ show: false, title: '', message: '' });
  const closeModal = () => setModal({ show: false, title: '', message: '' });
  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category: ''
  });
  const [selectedFile, setSelectedFile] = useState(null);

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products');
      setProducts(res.data);
    } catch (error) {
      setModal({ show: true, title: 'Помилка', message: 'Не вдалося завантажити товари' });
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleEdit = (prod) => {
    setEditMode(true);
    setEditId(prod._id);
    setFormData({
      name: prod.name,
      description: prod.description,
      price: prod.price,
      category: prod.category
    });
    setSelectedFile(null);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const data = new FormData();
      data.append('name', formData.name);
      data.append('description', formData.description);
      data.append('price', formData.price);
      data.append('category', formData.category);
      if (selectedFile) {
        data.append('image', selectedFile);
      }
      await axios.put(`http://localhost:5000/api/products/${editId}`, data, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setModal({ show: true, title: 'Успіх', message: 'Товар оновлено!' });
      setEditMode(false);
      setEditId(null);
      setFormData({ name: '', description: '', price: '', category: '' });
      setSelectedFile(null);
      fetchProducts();
    } catch (error) {
      setModal({ show: true, title: 'Помилка', message: 'Не вдалося оновити товар' });
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${id}`);
      setModal({ show: true, title: 'Успіх', message: 'Товар видалено' });
      fetchProducts();
    } catch (error) {
      setModal({ show: true, title: 'Помилка', message: 'Не вдалося видалити товар' });
    }
  };

  return (
    <div className="admin-container">
      <ModalMessage show={modal.show} onClose={closeModal} title={modal.title} message={modal.message} />
      <h2>Адмін-панель</h2>
      
      {/* Блок редагування товару (показується при редагуванні) */}
      {editMode && (
        <div className="edit-form-container">
          <h3>Редагувати товар</h3>
          <form onSubmit={handleUpdate} className="admin-form">
            <input type="text" name="name" placeholder="Назва" value={formData.name} onChange={handleChange} required />
            <input type="text" name="description" placeholder="Опис" value={formData.description} onChange={handleChange} />
            <input type="number" name="price" placeholder="Ціна" value={formData.price} onChange={handleChange} required />
            <input type="text" name="category" placeholder="Категорія" value={formData.category} onChange={handleChange} required />
            <input type="file" onChange={handleFileChange} />
            <button type="submit">Оновити товар</button>
            <button type="button" onClick={() => {
              setEditMode(false);
              setEditId(null);
              setFormData({ name: '', description: '', price: '', category: '' });
            }}>
              Скасувати
            </button>
          </form>
        </div>
      )}

      {/* Список товарів */}
      <div className="admin-products-list">
        {products.map(prod => (
          <div key={prod._id} className="admin-product-item">
            <h4>{prod.name} (${prod.price})</h4>
            <p>{prod.description}</p>
            {prod.imagePath && (
              <img src={`http://localhost:5000/${prod.imagePath}`} alt={prod.name} />
            )}
            <p><em>Категорія: {prod.category}</em></p>
            <div className="admin-item-buttons">
              <button onClick={() => handleEdit(prod)}>Редагувати</button>
              <button onClick={() => handleDelete(prod._id)}>Видалити</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default AdminPage;
